async def is_user_admin(client, chat_id, user_id):
    try:
        member = await client.get_chat_member(chat_id, user_id)
        return member.status in (ChatMemberStatus.ADMINISTRATOR, ChatMemberStatus.OWNER)
    except UserNotParticipant:
        return False


@app.on_message(filters.command(["gntag"], prefixes=["/", "!"]))
async def mention_all_gn(client, message):
    chat_id = message.chat.id
    if message.chat.type == ChatType.PRIVATE:
        return await message.reply("р╣П ЁЯЪи This command can only be used in groups! ЁЯЪи.")

    if not await is_user_admin(client, chat_id, message.from_user.id):
        return await message.reply("р╣П **Oops! You're not an admin, baby!** Only admins can tag members.")

    if chat_id in spam_chats:
        return await message.reply("р╣П тЪа Please stop the running mention process first before using this command! ЁЯЪл...")

    spam_chats.append(chat_id)
    try:
        async for member in client.get_chat_members(chat_id):
            if not chat_id in spam_chats:
                break
            if member.user.is_bot:
                continue
            await client.send_message(
                chat_id,
                f"[{member.user.first_name}](tg://user?id={member.user.id}) {random.choice(GN_MESSAGES)}"
            )
            await asyncio.sleep(4)
    finally:
        if chat_id in spam_chats:
            spam_chats.remove(chat_id)

